package jp.programmer.gi.lib.commons;import java.util.*;public final class Commons {	public static <T> Optional<T> cast(Object from, Class<T> to) {		T result = to.cast(from);		return Optional.ofNullable(result);	}	public static boolean isPlus(int value) {		return value > 0;	}	public static boolean isMinus(int value) {		return value < 0;	}	public static boolean notPlus(int value) {		return value <= 0;	}	public static boolean notMinus(int value) {		return value >= 0;	}}